import React, { useState } from 'react';
import axios from 'axios';

const ChatWithSource = () => {
  const [message, setMessage] = useState('');
  const [sourceName, setSourceName] = useState('public');
  const [response, setResponse] = useState('');
  const [references, setReferences] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');
    setResponse('');
    setReferences([]);

    try {
      const result = await axios.post('/api/chat-with-source/', {
        message: message,
        source_name: sourceName,
        filters: {} // You can add filters here if needed
      });

      setResponse(result.data.response.content);
      setReferences(result.data.response.references);
    } catch (err) {
      setError('An error occurred while fetching the response.');
      console.error('Error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="chat-with-source">
      <h2>Chat with Source</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="source-select">Select Source:</label>
          <select
            id="source-select"
            value={sourceName}
            onChange={(e) => setSourceName(e.target.value)}
          >
            <option value="public">Public</option>
            <option value="internal">Internal</option>
          </select>
        </div>
        <div>
          <label htmlFor="message-input">Enter your message:</label>
          <textarea
            id="message-input"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            rows="4"
            cols="50"
          />
        </div>
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Sending...' : 'Send Message'}
        </button>
      </form>

      {error && <p className="error">{error}</p>}

      {response && (
        <div className="response">
          <h3>Response:</h3>
          <p>{response}</p>
        </div>
      )}

      {references.length > 0 && (
        <div className="references">
          <h3>References:</h3>
          <ul>
            {references.map((ref, index) => (
              <li key={index}>{ref}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ChatWithSource;
