import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ListSources() {
  const [globalSources, setGlobalSources] = useState([]);
  const [privateSources, setPrivateSources] = useState([]);
  const [selectedSource, setSelectedSource] = useState(null);
  const [documents, setDocuments] = useState([]);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [sortField, setSortField] = useState('filename');
  const [sortDirection, setSortDirection] = useState('asc');

  const formatDate = (dateString) => {
    return dateString ? new Date(dateString).toLocaleString() : 'N/A';
  };

  const handleSort = (field) => {
    const direction = field === sortField ? (sortDirection === 'asc' ? 'desc' : 'asc') : 'asc';
    setSortField(field);
    setSortDirection(direction);
  };

  const sortDocuments = (docs) => {
    return docs.sort((a, b) => {
      let comparison = 0;
      if (a[sortField] < b[sortField]) {
        comparison = -1;
      } else if (a[sortField] > b[sortField]) {
        comparison = 1;
      }
      return sortDirection === 'asc' ? comparison : -comparison;
    });
  };

  const refreshData = async () => {
    await fetchSources();
    if (selectedSource) {
      await fetchDocuments(selectedSource);
    }
  };

  useEffect(() => {
    refreshData();
  }, []);

  const fetchSources = async () => {
    setIsLoading(true);
    setMessage('');

    try {
      const response = await axios.get('/chatbot1/list-sources/', {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.status === 200) {
        setGlobalSources(response.data.global_sources || []);
        setPrivateSources(response.data.private_sources || []);
      } else {
        setMessage('An error occurred while fetching the sources.');
      }
    } catch (error) {
      console.error('Error during source fetching:', error);
      setMessage(error.response?.data?.error || 'An error occurred while fetching the sources.');
    } finally {
      setIsLoading(false);
    }
  };

  const fetchDocuments = async (source) => {
    setIsLoading(true);
    setMessage('');
    setSelectedSource(source);

    try {
      const response = await axios.get(`/chatbot1/list-documents/${source}/`, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.status === 200 && response.data.documents) {
        setDocuments(sortDocuments(response.data.documents));
        setMessage(response.data.documents.length === 0 ? 'No documents found.' : '');
      } else {
        setMessage('An error occurred while fetching the documents.');
      }
    } catch (error) {
      console.error('Error during document fetching:', error);
      setMessage(error.response?.data?.error || 'An error occurred while fetching the documents.');
    } finally {
      setIsLoading(false);
    }
  };

  const getCookie = (name) => {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.substring(0, name.length + 1) === `${name}=`) {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  };

  return (
    <div>
      {/* Render source list */}
      {globalSources.length > 0 && renderSourceList(globalSources, 'Global Sources')}
      {privateSources.length > 0 && renderSourceList(privateSources, 'Private Sources')}

      {/* Display the documents in a table */}
      {selectedSource && (
        <div>
          <h2 style={{ marginTop: '20px' }}>Documents for Source: {selectedSource}</h2>
          {isLoading ? (
            <p>Loading documents...</p>
          ) : (
            <>
              {message && <p>{message}</p>}
              {documents.length > 0 && (
                <table>
                  <thead>
                    <tr>
                      <th onClick={() => handleSort('filename')}>Filename</th>
                      <th onClick={() => handleSort('created_at')}>Created Timestamp</th>
                      <th onClick={() => handleSort('modified_at')}>Edited Timestamp</th>
                      <th onClick={() => handleSort('visibility')}>Visibility</th>
                    </tr>
                  </thead>
                  <tbody>
                    {documents.map((doc, index) => (
                      <tr key={index}>
                        <td>{doc.filename}</td>
                        <td>{formatDate(doc.created_at)}</td>
                        <td>{formatDate(doc.modified_at)}</td>
                        <td>{doc.visibility}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default ListSources;
