import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom'; // Import the useLocation hook
import Header from './Header';
import ConversationBox from './ConversationBox';
import InputBox from './InputBox';

function Home() {
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [djangoResult, setDjangoResult] = useState(null);
  const [showSpinner, setShowSpinner] = useState(false);
  const [question, setQuestion] = useState('');
  const conversationBoxRef = useRef(null);
  const location = useLocation(); // Use the useLocation hook
  const searchParams = new URLSearchParams(location.search);
  const mysource = searchParams.get('mysource');

  useEffect(() => {
    if (conversationBoxRef.current) {
      conversationBoxRef.current.scrollTop = conversationBoxRef.current.scrollHeight;
    }

    const fetchDjangoResult = async () => {
      const djangoUrl = '/add-one/';
      const data = { number: 5 };

      try {
        const djangoResp = await axios.post(djangoUrl, data);
        const djangoResult = djangoResp.data.result;
        setDjangoResult(djangoResult);
      } catch (error) {
        console.error(error);
      }
    };

    fetchDjangoResult();
  }, []);

  const handleSendMessage = async (e) => {
    // Rest of your code
  };

  return (
    <div className="Home">
      <Header djangoResult={djangoResult} />
      <ConversationBox messages={messages} isLoading={isLoading} conversationBoxRef={conversationBoxRef} />
      <InputBox question={question} setQuestion={setQuestion} handleSendMessage={handleSendMessage} isLoading={isLoading} showSpinner={showSpinner} />
    </div>
  );
}

export default Home;
