// Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from './AuthContext';

function Home() {
  const { user } = useAuth();

  return (
    <div style={{
      backgroundColor: '#f4f4f4',
      color: '#333',
      height: '100vh',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      textAlign: 'center'
    }}>
      <h1>Welcome to the Chatbot App</h1>
      {user ? (
        <>
          <p>Hello, {user.username}!</p>
          <p>Choose a chatbot, create a new source, view all sources, or manage auto-uploads:</p>
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            <li style={{ margin: '10px 0' }}>
              <Link to="/public" style={{ color: '#007bff', textDecoration: 'none' }}>Public Chatbot</Link>
            </li>
            <li style={{ margin: '10px 0' }}>
              <Link to="/internal" style={{ color: '#007bff', textDecoration: 'none' }}>Internal Chatbot</Link>
            </li>
            <li style={{ margin: '10px 0' }}>
              <Link to="/create-source" style={{ color: '#007bff', textDecoration: 'none' }}>Create a New Source</Link>
            </li>
            <li style={{ margin: '10px 0' }}>
              <Link to="/sources" style={{ color: '#007bff', textDecoration: 'none' }}>View All Sources</Link>
            </li>
            <li style={{ margin: '10px 0' }}>
              <Link to="/auto-upload" style={{ color: '#007bff', textDecoration: 'none' }}>
                Manage Auto-Uploads
              </Link>
            </li>
          </ul>
        </>
      ) : (
        <p>Please <Link to="/login" style={{ color: '#007bff', textDecoration: 'none' }}>login</Link> to access the app features.</p>
      )}
    </div>
  );
}

export default Home;
