// UploadDocument.js
import React, { useState } from 'react';
import axios from 'axios';

const UploadDocument = () => {
    const [file, setFile] = useState(null);
    const [sourceName, setSourceName] = useState('');
    const [uploadStatus, setUploadStatus] = useState('');

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleSourceNameChange = (event) => {
        setSourceName(event.target.value);
    };

    const handleUpload = async (event) => {
        event.preventDefault();
        if (!file || !sourceName) {
            setUploadStatus('Please select a file and provide a source name.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await axios.post(
                `http://localhost:8001/chatbot1/api/upload-document/${sourceName}/`,
                formData,
                {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                }
            );
            if (response.data.status === 'success') {
                setUploadStatus('File uploaded successfully!');
            } else {
                setUploadStatus(`Upload failed: ${response.data.message}`);
            }
        } catch (error) {
            setUploadStatus(`Upload error: ${error.message}`);
        }
    };

    return (
        <div>
            <h1>Upload Document</h1>
            <form onSubmit={handleUpload}>
                <div>
                    <label>Source Name:</label>
                    <input
                        type="text"
                        value={sourceName}
                        onChange={handleSourceNameChange}
                        placeholder="Enter source name"
                    />
                </div>
                <div>
                    <label>Choose File:</label>
                    <input
                        type="file"
                        onChange={handleFileChange}
                    />
                </div>
                <div>
                    <button type="submit">Upload</button>
                </div>
            </form>
            {uploadStatus && <p>{uploadStatus}</p>}
        </div>
    );
};

export default UploadDocument;
