import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const AUTO_UPLOAD_URL = '/chatbot1/auto-upload/';

function AutoUploadManager() {
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [processedFiles, setProcessedFiles] = useState([]);

  const getCookie = (name) => {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.substring(0, name.length + 1) === `${name}=`) {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  };

  const handleAutoUpload = useCallback(async () => {
    setIsLoading(true);
    setMessage('');

    try {
      const response = await axios.post(AUTO_UPLOAD_URL, {}, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.status === 200) {
        setProcessedFiles(response.data.processed_files || []);
        setMessage('Auto-upload process completed successfully.');
      } else {
        setMessage('An unexpected error occurred during auto-upload.');
      }
    } catch (error) {
      console.error('Error during auto-upload:', error);
      setMessage(error.response?.data?.message || error.message || 'An error occurred during auto-upload.');
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      handleAutoUpload();
    }, 5 * 60 * 1000); // Run every 5 minutes

    return () => clearInterval(interval);
  }, [handleAutoUpload]);

  return (
    <div style={{ padding: '20px', backgroundColor: 'white', maxWidth: '800px', margin: '0 auto' }}>
      <h2>Auto-Upload Manager</h2>
      <button
        onClick={handleAutoUpload}
        disabled={isLoading}
        style={{
          marginBottom: '20px',
          padding: '10px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        {isLoading ? 'Processing...' : 'Run Auto-Upload'}
      </button>
      {message && <p style={{ color: 'green' }}>{message}</p>}
      {processedFiles.length > 0 && (
        <div>
          <h3>Processed Files:</h3>
          <ul>
            {processedFiles.map((file, index) => (
              <li key={index}>
                {file.filename} (Source: {file.source}): {file.status}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default AutoUploadManager;
