import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ListSources() {
  const [sources, setSources] = useState([]);
  const [selectedSource, setSelectedSource] = useState(null);
  const [documents, setDocuments] = useState([]);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadStatus, setUploadStatus] = useState('');
  const [uploadSteps, setUploadSteps] = useState([]);
  const [sortField, setSortField] = useState('name');
  const [sortDirection, setSortDirection] = useState('asc');

  const formatDate = (dateString) => {
    if (dateString && dateString !== '') {
      return new Date(dateString).toLocaleString();
    }
    return 'N/A';
  };

  const handleSort = (field) => {
    if (field === sortField) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('asc');
    }
  };

  const sortSources = (sourcesToSort) => {
    return sourcesToSort.sort((a, b) => {
      let comparison = 0;
      if (a[sortField] < b[sortField]) {
        comparison = -1;
      } else if (a[sortField] > b[sortField]) {
        comparison = 1;
      }
      return sortDirection === 'asc' ? comparison : -comparison;
    });
  };

  const refreshData = async () => {
    await fetchSources();
  };

  useEffect(() => {
    refreshData();
  }, []);

  const fetchSources = async () => {
    setIsLoading(true);
    setMessage('');

    try {
      const response = await axios.get('/chatbot1/list-sources/', {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.status === 200) {
        setSources(response.data.sources || []);
      } else {
        setMessage('An error occurred while fetching the sources.');
      }
    } catch (error) {
      console.error('Error during source fetching:', error);
      setMessage(error.response?.data?.error || 'An error occurred while fetching the sources.');
    } finally {
      setIsLoading(false);
    }
  };

  const fetchDocuments = async (source) => {
    setIsLoading(true);
    setMessage('');
    setSelectedSource(source);

    try {
      const response = await axios.get(`/chatbot1/list-documents/${source}/`, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.status === 200 && response.data.documents) {
        setDocuments(response.data.documents.documents);
        setMessage(response.data.documents.documents.length === 0 ? 'No documents found.' : '');
      } else {
        setMessage('An error occurred while fetching the documents.');
      }
    } catch (error) {
      console.error('Error during document fetching:', error);
      setMessage(error.response?.data?.error || 'An error occurred while fetching the documents.');
    } finally {
      setIsLoading(false);
    }
  };

  const getCookie = (name) => {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.substring(0, name.length + 1) === `${name}=`) {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  };

  const handleSyncSource = async (sourceName) => {
    try {
      const response = await axios.post(`/chatbot1/sync-source/${sourceName}/`, {}, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });
      alert(response.data.message);
      fetchSources();
    } catch (error) {
      console.error('Error syncing source:', error);
      console.error('Error details:', error.response?.data);
      alert(error.response?.data?.error || 'An error occurred during synchronization. Please check the server logs for more details.');
    }
  };

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile || !selectedSource) {
      setMessage('Please select a file and a source to upload.');
      return;
    }

    setIsLoading(true);
    setMessage('');
    setUploadProgress(0);
    setUploadStatus('Preparing upload...');
    setUploadSteps([]);

    try {
      const formData = new FormData();
      formData.append('file', selectedFile);

      const response = await axios.post(`/chatbot1/upload-document/${selectedSource}/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true,
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setUploadProgress(percentCompleted);
          setUploadStatus(`Uploading: ${percentCompleted}%`);
        }
      });

      console.log('Full API Response:', response.data);

      if (response.status === 200 || response.status === 201) {
        setUploadStatus('Upload successful. Processing document...');
        setUploadSteps(prevSteps => [...prevSteps, 'Document uploaded successfully']);
        await pollUploadStatus(selectedSource, response.data.task_id);
      } else if (response.status === 202) {
        setUploadStatus('Document replaced successfully. Processing...');
        setUploadSteps(prevSteps => [...prevSteps, 'Document replaced successfully']);
        await pollUploadStatus(selectedSource, response.data.task_id);
      } else {
        setUploadStatus('Upload failed');
        setMessage('An error occurred while uploading the document.');
      }
    } catch (error) {
      console.error('Error during document upload:', error);
      setUploadStatus('Upload failed');
      setMessage(error.response?.data?.error || 'An error occurred while uploading the document.');
    } finally {
      setIsLoading(false);
      setUploadProgress(0);
      setSelectedFile(null);
    }
  };

  const pollUploadStatus = async (source, taskId, maxAttempts = 10) => {
    for (let i = 0; i < maxAttempts; i++) {
      try {
        const response = await axios.get(`/chatbot1/check-upload-status/${source}/${taskId}/`, {
          headers: {
            'X-CSRFToken': getCookie('csrftoken'),
          },
          withCredentials: true
        });

        console.log('Poll response:', response.data);

        if (response.data.status === 'SUCCESS') {
          setUploadStatus('Upload and processing completed successfully');
          setUploadSteps(prevSteps => [...prevSteps, 'Document processed successfully']);
          setMessage(`Upload completed successfully.\nFull Response: ${JSON.stringify(response.data.full_response, null, 2)}`);
          await fetchDocuments(source);
          return;
        } else if (response.data.status === 'ERROR') {
          setUploadStatus('Upload failed');
          setUploadSteps(prevSteps => [...prevSteps, 'Error during document processing']);
          setMessage(`Upload failed.\nError: ${response.data.message}\nFull Response: ${JSON.stringify(response.data.full_response, null, 2)}`);
          return;
        }

        setUploadStatus(`Processing document... (Attempt ${i + 1}/${maxAttempts})`);
        setUploadSteps(prevSteps => [...prevSteps, `Processing attempt ${i + 1}`]);
        await new Promise(resolve => setTimeout(resolve, 5000));
      } catch (error) {
        console.error('Error polling upload status:', error);
        setUploadStatus('Error checking upload status');
        setMessage(`Error checking upload status: ${error.message}`);
        return;
      }
    }

    setUploadStatus('Upload status check timed out');
    setMessage('Upload status check timed out. The upload may still be in progress.');
  };

  const renderSourcesTable = () => (
    <div>
      <h2 style={{ color: '#444', marginTop: '20px' }}>Sources</h2>
      {sources.length > 0 ? (
        <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
          <thead>
            <tr style={{ backgroundColor: '#f8f9fa' }}>
              <th style={tableHeaderStyle} onClick={() => handleSort('name')}>
                Source Name {sortField === 'name' && (sortDirection === 'asc' ? '▲' : '▼')}
              </th>
              <th style={tableHeaderStyle} onClick={() => handleSort('visibility')}>
                Visibility {sortField === 'visibility' && (sortDirection === 'asc' ? '▲' : '▼')}
              </th>
              <th style={tableHeaderStyle} onClick={() => handleSort('model')}>
                Model {sortField === 'model' && (sortDirection === 'asc' ? '▲' : '▼')}
              </th>
              <th style={tableHeaderStyle} onClick={() => handleSort('created_at')}>
                Created Date {sortField === 'created_at' && (sortDirection === 'asc' ? '▲' : '▼')}
              </th>
              <th style={tableHeaderStyle} onClick={() => handleSort('updated_at')}>
                Updated Date {sortField === 'updated_at' && (sortDirection === 'asc' ? '▲' : '▼')}
              </th>
              <th style={tableHeaderStyle}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {sortSources(sources).map((source, index) => (
              <tr key={source.id || index}>
                <td style={tableCellStyle}>{source.name}</td>
                <td style={tableCellStyle}>{source.visibility}</td>
                <td style={tableCellStyle}>{source.model}</td>
                <td style={tableCellStyle}>{formatDate(source.created_at)}</td>
                <td style={tableCellStyle}>{formatDate(source.updated_at)}</td>
                <td style={tableCellStyle}>
                  <button onClick={() => fetchDocuments(source.name)} style={buttonStyle}>
                    View Documents
                  </button>
                  <button onClick={() => handleSyncSource(source.name)} style={buttonStyle}>
                    Sync Source
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No sources found.</p>
      )}
    </div>
  );

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', maxWidth: '1200px', margin: '0 auto', padding: '20px' }}>
      <h1 style={{ color: '#333', borderBottom: '2px solid #333', paddingBottom: '10px' }}>Document Sources</h1>

      {isLoading ? (
        <p>Loading...</p>
      ) : (
        renderSourcesTable()
      )}

      {selectedSource && (
        <div>
          <h2>Documents in {selectedSource}</h2>
          {documents.length > 0 ? (
            <ul>
              {documents.map((doc, index) => (
                <li key={index}>{doc.filename}</li>
              ))}
            </ul>
          ) : (
            <p>No documents found in this source.</p>
          )}
        </div>
      )}

      <div style={{ marginTop: '20px' }}>
        <h2>Upload Document</h2>
        <input type="file" onChange={handleFileChange} style={{ marginBottom: '10px' }} />
        <button onClick={handleUpload} disabled={!selectedFile || !selectedSource} style={buttonStyle}>
          Upload
        </button>
        {uploadProgress > 0 && <progress value={uploadProgress} max="100" style={{ width: '100%' }} />}
        {uploadStatus && <p>{uploadStatus}</p>}
        {uploadSteps.length > 0 && (
          <ul>
            {uploadSteps.map((step, index) => (
              <li key={index}>{step}</li>
            ))}
          </ul>
        )}
      </div>

      {message && <p style={{ color: 'red', marginTop: '20px' }}>{message}</p>}
    </div>
  );
}

const tableHeaderStyle = {
  border: '1px solid #dee2e6',
  padding: '12px',
  textAlign: 'left',
  cursor: 'pointer'
};

const tableCellStyle = {
  border: '1px solid #dee2e6',
  padding: '12px'
};

const buttonStyle = {
  backgroundColor: '#007bff',
  color: 'white',
  padding: '5px 10px',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  marginRight: '5px'
};

export default ListSources;
