// App.js
import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './Home';
import Chatbot from './Chatbot';
import CreateSource from './CreateSource';
import SourceList from './ListSources';
import UploadDocument from './UploadDocument';
import './App.css';
import { handleAutoUpload, startAutoUploadInterval, stopAutoUploadInterval } from './AutoUploadManager';

function App() {
  useEffect(() => {
    // Start the auto-upload interval when the app loads
    const intervalId = startAutoUploadInterval(5); // Auto-upload every 5 minutes

    // Optionally, you can also trigger an immediate upload
    handleAutoUpload();

    // Clean up the interval when the component unmounts
    return () => {
      stopAutoUploadInterval(intervalId);
    };
  }, []);

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/:mysource" element={<Chatbot />} />
          <Route path="/create-source" element={<CreateSource />} />
          <Route path="/sources" element={<SourceList />} />
          <Route path="/sources/:source/upload" element={<UploadDocument />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
