import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ITEMS_PER_PAGE = 50; // Adjust this number as needed

const ListSources = () => {
  const [sources, setSources] = useState([]);
  const [selectedSource, setSelectedSource] = useState('');
  const [documents, setDocuments] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    fetchSources();
  }, []);

  useEffect(() => {
    console.log('Documents state changed:', documents);
    if (documents && documents.documents) {
      console.log('Source:', documents.documents.source);
      console.log('Count:', documents.documents.count);
      console.log('Documents list:', documents.documents.documents);
    }
  }, [documents]);

  const fetchSources = async () => {
    try {
      const response = await axios.get('http://localhost:5000/list_sources');
      setSources(response.data.sources);
    } catch (error) {
      console.error('Error fetching sources:', error);
    }
  };

  const fetchDocuments = async (source) => {
    try {
      console.log('Fetching documents for source:', source);
      const response = await axios.get(`http://localhost:5000/list_documents/${source}`);
      console.log('Raw response:', response);
      console.log('Response status:', response.status);
      console.log('Response data:', response.data);
      setDocuments(response.data);
      setCurrentPage(1); // Reset to first page when fetching new documents
    } catch (error) {
      console.error('Error fetching documents:', error);
    }
  };

  const handleSourceChange = (event) => {
    const source = event.target.value;
    setSelectedSource(source);
    if (source) {
      fetchDocuments(source);
    } else {
      setDocuments(null);
    }
  };

  const renderDocuments = () => {
    console.log('Rendering documents. Current documents state:', documents);

    if (!documents || !documents.documents) {
      console.log('No documents in state or unexpected structure');
      return <p>No documents found in this source.</p>;
    }

    const { source, count, documents: documentsList } = documents.documents;

    console.log('Source:', source);
    console.log('Count:', count);
    console.log('Documents list:', documentsList);

    const indexOfLastItem = currentPage * ITEMS_PER_PAGE;
    const indexOfFirstItem = indexOfLastItem - ITEMS_PER_PAGE;
    const currentItems = documentsList.slice(indexOfFirstItem, indexOfLastItem);

    return (
      <div>
        <h2 style={{ color: '#444', marginTop: '20px' }}>Documents in {source}</h2>
        <p>Total documents: {count}</p>
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {Array.isArray(currentItems) && currentItems.length > 0 ? (
            currentItems.map((document, index) => (
              <li key={document.id || index} style={{ marginBottom: '10px', border: '1px solid #ccc', padding: '10px', borderRadius: '4px' }}>
                <h4 style={{ margin: '0' }}>{document.filename}</h4>
                <p>ID: {document.id}</p>
                <p>Chunks: {document.chunks}</p>
                <p>Chunk Size: {document.chunk_size}</p>
                <p>Chunk Overlap: {document.chunk_overlap}</p>
              </li>
            ))
          ) : (
            <li>No documents available.</li>
          )}
        </ul>
        {renderPagination(count)}
      </div>
    );
  };

  const renderPagination = (totalItems) => {
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(totalItems / ITEMS_PER_PAGE); i++) {
      pageNumbers.push(i);
    }

    return (
      <nav>
        <ul className='pagination' style={{ listStyle: 'none', display: 'flex', justifyContent: 'center', padding: 0 }}>
          {pageNumbers.map(number => (
            <li key={number} className='page-item' style={{ margin: '0 5px' }}>
              <button onClick={() => setCurrentPage(number)} className='page-link' style={{ padding: '5px 10px', border: '1px solid #007bff', borderRadius: '3px', background: currentPage === number ? '#007bff' : 'white', color: currentPage === number ? 'white' : '#007bff' }}>
                {number}
              </button>
            </li>
          ))}
        </ul>
      </nav>
    );
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
      <h1 style={{ color: '#333', borderBottom: '2px solid #333', paddingBottom: '10px' }}>Document Sources</h1>
      <select
        value={selectedSource}
        onChange={handleSourceChange}
        style={{ width: '100%', padding: '10px', marginBottom: '20px', fontSize: '16px' }}
      >
        <option value="">Select a source</option>
        {sources.map((source, index) => (
          <option key={index} value={source}>
            {source}
          </option>
        ))}
      </select>
      {documents && renderDocuments()}
    </div>
  );
};

export default ListSources;
