// UploadDocument.js
import React, { useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function UploadDocument() {
  const [file, setFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [splitPages, setSplitPages] = useState(false);
  const [customFields, setCustomFields] = useState('');
  const [progress, setProgress] = useState(0);
  const { source } = useParams();
  const navigate = useNavigate();

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.size > 10 * 1024 * 1024) {
      alert('File is too large. Maximum size is 10MB.');
      e.target.value = null;
    } else {
      setFile(selectedFile);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      alert('Please select a file to upload');
      return;
    }

    setIsLoading(true);
    setUploadStatus('Initiating upload...');
    setProgress(10);
    const formData = new FormData();
    formData.append('file', file);
    formData.append('split_pages', splitPages);
    if (customFields) formData.append('custom_fields', customFields);

    try {
      const response = await axios.post(`/api/v1/sources/${source}/documents`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true,
      });

      if (response.status === 202) {
        setUploadStatus('Upload accepted and is being processed.');
        setProgress(30);
        if (response.data && response.data.task_id) {
          await checkDocumentStatus(response.data.task_id);
        } else {
          await pollForDocument(source, file.name);
        }
      } else if (response.status === 201) {
        setUploadStatus('Upload completed successfully.');
        setProgress(100);
        setTimeout(() => navigate(`/sources/${source}`), 2000);
      } else {
        setUploadStatus('Unexpected response. Please check the server logs.');
        setProgress(0);
      }
    } catch (error) {
      console.error('Error uploading document:', error);
      setUploadStatus('Error: ' + (error.response?.data?.detail || error.message || 'Unknown error occurred'));
      setProgress(0);
    } finally {
      setIsLoading(false);
    }
  };

  const checkDocumentStatus = async (taskId) => {
    try {
      const response = await axios.get(`/api/check-document-status/${taskId}/`, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.data.status === "PENDING") {
        setUploadStatus('Still processing...');
        setProgress(60);
        setTimeout(() => checkDocumentStatus(taskId), 5000);  // Check every 5 seconds
      } else {
        updateUIWithStatus(response.data.status);
      }
    } catch (error) {
      console.error('Error checking status:', error);
      setUploadStatus('Error checking status. Please try again.');
      setProgress(0);
    }
  };

  const updateUIWithStatus = (status) => {
    if (status === "COMPLETE") {
      setUploadStatus('Document upload complete!');
      setProgress(100);
      setTimeout(() => navigate(`/sources/${source}`), 2000);
    } else if (status === "FAILED") {
      setUploadStatus('Document upload failed.');
      setProgress(0);
    } else {
      setUploadStatus(`Unknown status: ${status}`);
      setProgress(0);
    }
  };

  const pollForDocument = async (sourceName, fileName, attempts = 0) => {
    if (attempts > 12) {
      setUploadStatus('Document processing is taking longer than expected. Please check back later.');
      setProgress(0);
      return;
    }

    try {
      const response = await axios.get(`/api/v1/sources/${sourceName}/documents`, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.status === 200) {
        const documents = response.data.documents;
        console.log('Received documents:', documents);
        if (documents.some(doc => doc.filename === fileName)) {
          setUploadStatus('Document processed successfully.');
          setProgress(100);
          setTimeout(() => navigate(`/sources/${sourceName}`), 2000);
        } else {
          setUploadStatus(`Processing document... (Attempt ${attempts + 1})`);
          setProgress(30 + attempts * 5);  // Increase progress with each attempt
          setTimeout(() => pollForDocument(sourceName, fileName, attempts + 1), 5000);
        }
      } else {
        setUploadStatus('Error checking document status. Please refresh the page.');
        setProgress(0);
      }
    } catch (error) {
      console.error('Error polling for document:', error);
      setUploadStatus('Error checking document status. Please refresh the page.');
      setProgress(0);
    }
  };

  function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.substring(0, name.length + 1) === (name + '=')) {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  }

  return (
    <div>
      <h2>Upload Document to {source}</h2>
      <form onSubmit={handleUpload}>
        <input type="file" onChange={handleFileChange} />
        <label>
          <input type="checkbox" checked={splitPages} onChange={(e) => setSplitPages(e.target.checked)} />
          Split Pages
        </label>
        <textarea
          placeholder="Custom Fields (JSON)"
          value={customFields}
          onChange={(e) => setCustomFields(e.target.value)}
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Uploading...' : 'Upload'}
        </button>
      </form>
      {uploadStatus && <p>{uploadStatus}</p>}
      <div style={{width: '300px', height: '20px', backgroundColor: '#f0f0f0', borderRadius: '10px', margin: '10px 0'}}>
        <div style={{width: `${progress}%`, height: '100%', backgroundColor: '#4CAF50', borderRadius: '10px', transition: 'width 0.5s'}}></div>
      </div>
      <button onClick={() => navigate(`/sources/${source}`)}>Back to Documents</button>
    </div>
  );
}

export default UploadDocument;
