import React, { useState, useCallback, useEffect } from 'react';
import axios from 'axios';

const AUTO_UPLOAD_URL = '/chatbot1/auto-upload/';
const START_SCHEDULER_URL = '/chatbot1/start-scheduler/';
const PROCESS_DOCUMENTS_URL = '/chatbot1/process-documents/';

function AutoUploadManager() {
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [schedulerStatus, setSchedulerStatus] = useState('Not running');
  const [uploadDetails, setUploadDetails] = useState([]);

  useEffect(() => {
    axios.defaults.withCredentials = true;
    // Fetch initial upload status when component mounts
    fetchUploadStatus();
  }, []);

  const fetchUploadStatus = async () => {
    try {
      const response = await axios.get(PROCESS_DOCUMENTS_URL, {
        headers: {
          'X-CSRFToken': getCookie('csrftoken'),
        },
      });
      if (response.status === 200) {
        setUploadDetails(response.data.processed_files || []);
      }
    } catch (error) {
      console.error('Error fetching upload status:', error);
    }
  };

  const handleAutoUpload = useCallback(async () => {
    setIsLoading(true);
    setMessage('');

    try {
      const response = await axios.post(AUTO_UPLOAD_URL, {}, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
      });

      if (response.status === 200) {
        setMessage('Auto-upload process completed successfully.');
        setUploadedFiles(response.data.processed_files || []);
        await fetchUploadStatus(); // Fetch updated status after auto-upload
      } else {
        setMessage('An unexpected error occurred during auto-upload.');
      }
    } catch (error) {
      console.error('Error during auto-upload:', error);
      setMessage(error.response?.data?.error || error.message || 'An error occurred during auto-upload.');
    } finally {
      setIsLoading(false);
    }
  }, []);

  const startScheduler = useCallback(async () => {
    try {
      const response = await axios.post(START_SCHEDULER_URL, {}, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
      });

      if (response.status === 200) {
        setSchedulerStatus('Running');
        setMessage(response.data.message);
      } else {
        setMessage('An unexpected error occurred while starting the scheduler.');
      }
    } catch (error) {
      console.error('Error starting scheduler:', error);
      setMessage(error.response?.data?.error || error.message || 'An error occurred while starting the scheduler.');
    }
  }, []);

  const getCookie = (name) => {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.substring(0, name.length + 1) === (name + '=')) {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  };

  return (
    <div>
      <h2>Auto Upload Manager</h2>
      <button onClick={handleAutoUpload} disabled={isLoading}>
        {isLoading ? 'Processing...' : 'Start Auto Upload'}
      </button>
      <button onClick={startScheduler}>Start Scheduler</button>
      <p>Scheduler Status: {schedulerStatus}</p>
      {message && <p>{message}</p>}
      {uploadedFiles.length > 0 && (
        <div>
          <h3>Recently Uploaded Files:</h3>
          <ul>
            {uploadedFiles.map((file, index) => (
              <li key={index}>{file}</li>
            ))}
          </ul>
        </div>
      )}
      {uploadDetails.length > 0 && (
        <div>
          <h3>Upload Details:</h3>
          <table>
            <thead>
              <tr>
                <th>File Name</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {uploadDetails.map((file, index) => (
                <tr key={index}>
                  <td>{file}</td>
                  <td>Processed</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default AutoUploadManager;
