import React, { useState } from 'react';
import axios from 'axios';

const ChatWithSource = () => {
  const [messages, setMessages] = useState([]);
  const [sourceName, setSourceName] = useState('oad-public');
  const [newMessage, setNewMessage] = useState('');
  const [k, setK] = useState(1);
  const [chatModel, setChatModel] = useState('gpt-4-32k');
  const [isStreaming, setIsStreaming] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    const payload = {
      messages: [...messages, { role: 'user', content: newMessage }],
      source_name: sourceName,
      filters: '{}',
      k: k,
      chat_model: chatModel,
      stream: isStreaming
    };

    try {
      if (isStreaming) {
        const response = await axios.post('/api/chat-with-source/', payload, {
          responseType: 'stream'
        });
        // Handle streaming response
        const reader = response.data.getReader();
        let result = '';
        while (true) {
          const { done, value } = await reader.read();
          if (done) break;
          result += new TextDecoder().decode(value);
          // Update UI with partial results
        }
        // Process final result
      } else {
        const response = await axios.post('/api/chat-with-source/', payload);
        setMessages([...messages,
          { role: 'user', content: newMessage },
          { role: 'assistant', content: response.data.content }
        ]);
      }
      setNewMessage('');
    } catch (err) {
      setError('An error occurred while fetching the response.');
      console.error('Error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="chat-with-source">
      <h2>Chat with Source</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="source-select">Select Source:</label>
          <select
            id="source-select"
            value={sourceName}
            onChange={(e) => setSourceName(e.target.value)}
          >
            <option value="oad-public">Public</option>
            <option value="oad-internal">Internal</option>
          </select>
        </div>
        <div>
          <label htmlFor="k-input">Number of hits (k):</label>
          <input
            id="k-input"
            type="number"
            value={k}
            onChange={(e) => setK(parseInt(e.target.value))}
            min="1"
          />
        </div>
        <div>
          <label htmlFor="chat-model-select">Chat Model:</label>
          <select
            id="chat-model-select"
            value={chatModel}
            onChange={(e) => setChatModel(e.target.value)}
          >
            <option value="gpt-4-32k">GPT-4 32k</option>
            <option value="gpt-4o">GPT-4o</option>
            <option value="gpt-3.5-turbo-16k">GPT-3.5 Turbo 16k</option>
            <option value="claude-3-haiku">Claude 3 Haiku</option>
            <option value="claude-3-sonnet">Claude 3 Sonnet</option>
          </select>
        </div>
        <div>
          <label htmlFor="streaming-checkbox">
            <input
              id="streaming-checkbox"
              type="checkbox"
              checked={isStreaming}
              onChange={(e) => setIsStreaming(e.target.checked)}
            />
            Enable streaming
          </label>
        </div>
        <div>
          <label htmlFor="message-input">Enter your message:</label>
          <textarea
            id="message-input"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            rows="4"
            cols="50"
          />
        </div>
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Sending...' : 'Send Message'}
        </button>
      </form>

      {error && <p className="error">{error}</p>}

      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.role}`}>
            <strong>{msg.role}:</strong> {msg.content}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ChatWithSource;
