import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ChatWithSource = () => {
    const { sourceName } = useParams();
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [error, setError] = useState(null);

    useEffect(() => {
        // Reset messages and error when sourceName changes
        setMessages([]);
        setError(null);
    }, [sourceName]);

    const sendMessage = async () => {
        if (!input) return;

        const newMessage = {
            text: input,
            user: 'Me'
        };
        setMessages([...messages, newMessage]);
        setInput('');
        setError(null);  // Clear any previous errors

        try {
            const response = await axios.post(`/chat-with-source/${sourceName}/`, {
                question: input,
                filters: JSON.stringify({})  // Ensure filters are sent as JSON string
            });
            console.log('Received response from API:', response.data);

            if (response.data.error) {
                // Handle error from the API
                setError(response.data.error);
                setMessages([...messages, newMessage, { text: `Error: ${response.data.error}`, user: 'Bot' }]);
            } else {
                let formattedContent = formatResponse(response.data.response.content);
                setMessages([...messages, newMessage, { text: formattedContent, user: 'Bot' }]);
            }
        } catch (error) {
            console.error('Error sending message:', error);
            let errorMessage = 'Sorry, an error occurred while processing your request.';
            if (error.response) {
                // The request was made and the server responded with a status code
                // that falls out of the range of 2xx
                errorMessage = `Error: ${error.response.data.error || error.response.statusText}`;
                if (error.response.data.details) {
                    errorMessage += ` Details: ${error.response.data.details}`;
                }
            } else if (error.request) {
                // The request was made but no response was received
                errorMessage = 'No response received from the server.';
            } else {
                // Something happened in setting up the request that triggered an Error
                errorMessage = error.message;
            }
            setError(errorMessage);
            setMessages([...messages, newMessage, { text: errorMessage, user: 'Bot' }]);
        }
    };

    const formatResponse = (response) => {
        // ... (keep your existing formatting logic)
    };

    return (
        <div>
            <h2>Chat with Source: {sourceName}</h2>
            {error && <div style={{color: 'red'}}>{error}</div>}
            <div>
                {messages.map((message, index) => (
                    <div key={index}>
                        <strong>{message.user}:</strong> <span dangerouslySetInnerHTML={{ __html: message.text }} />
                    </div>
                ))}
            </div>
            <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyPress={(e) => (e.key === 'Enter' ? sendMessage() : null)}
            />
            <button onClick={sendMessage}>Send</button>
        </div>
    );
};

export default ChatWithSource;
