import React from 'react';
import Message from './Message';

function ConversationBox({ messages, isLoading, conversationBoxRef, source }) {
  return (
    <div className="conversation-box" ref={conversationBoxRef}>
      {messages.map((message, index) => (
        <Message
          key={index}
          message={message}
          isLoading={isLoading}
          index={index}
          messages={messages}
          source={source} // Pass source to Message component
        />
      ))}
    </div>
  );
}

export default ConversationBox;
