import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ListSources() {
  const [globalSources, setGlobalSources] = useState([]);
  const [privateSources, setPrivateSources] = useState([]);
  const [selectedSource, setSelectedSource] = useState(null);
  const [documents, setDocuments] = useState(null);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    fetchSources();
  }, []);

  const fetchSources = async () => {
    setIsLoading(true);
    setMessage('');

    try {
      const response = await axios.get('/chatbot1/list-sources/', {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.status === 200) {
        setGlobalSources(response.data.global_sources || []);
        setPrivateSources(response.data.private_sources || []);
      } else {
        setMessage('An error occurred while fetching the sources.');
      }
    } catch (error) {
      console.error('Error during source fetching:', error);
      setMessage(error.response?.data?.error || 'An error occurred while fetching the sources.');
    } finally {
      setIsLoading(false);
    }
  };

  const fetchDocuments = async (source) => {
    setIsLoading(true);
    setMessage('');
    setSelectedSource(source);

    try {
      const response = await axios.get(`/chatbot1/list-documents/${source}/`, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      console.log('Fetch documents response:', response.data);

      if (response.status === 200) {
        setDocuments(response.data.documents);
        if (response.data.documents.count === 0) {
          setMessage('No documents found.');
        } else {
          setMessage('');
        }
      } else {
        setMessage('An error occurred while fetching documents.');
      }
    } catch (error) {
      console.error('Error during document fetching:', error);
      setMessage(error.response?.data?.error || 'An error occurred while fetching documents.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleFileUpload = async () => {
    if (!selectedFile || !selectedSource) {
      setMessage('Please select a file and source.');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post(`/chatbot1/upload-document/${selectedSource}/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      console.log('Upload response:', response.data);

      if (response.status === 200) {
        setMessage(response.data.message);
        fetchDocuments(selectedSource);  // Refresh document list after upload
      } else {
        setMessage('An error occurred while uploading the document.');
      }
    } catch (error) {
      console.error('Error during file upload:', error);
      setMessage(error.response?.data?.error || 'An error occurred while uploading the document.');
    }
  };

  const getCookie = (name) => {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.substring(0, name.length + 1) === (name + '=')) {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  };

  return (
    <div>
      <h1>List Sources</h1>

      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <>
          <div>
            <h2>Global Sources</h2>
            <ul>
              {globalSources.map(source => (
                <li key={source} onClick={() => fetchDocuments(source)}>{source}</li>
              ))}
            </ul>
          </div>

          <div>
            <h2>Private Sources</h2>
            <ul>
              {privateSources.map(source => (
                <li key={source} onClick={() => fetchDocuments(source)}>{source}</li>
              ))}
            </ul>
          </div>

          {selectedSource && (
            <div>
              <h2>Documents in {selectedSource}</h2>
              {documents && (
                <ul>
                  {documents.documents.map((doc, index) => (
                    <li key={index}>
                      <a href={doc.file_url} target="_blank" rel="noopener noreferrer">{doc.filename}</a>
                    </li>
                  ))}
                </ul>
              )}
              {message && <p>{message}</p>}
              <input type="file" onChange={handleFileChange} />
              <button onClick={handleFileUpload}>Upload Document</button>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default ListSources;
