import React, { useState } from 'react';
import axios from 'axios';

const UploadDocument = () => {
    const [file, setFile] = useState(null);
    const [sourceName, setSourceName] = useState('');
    const [documents, setDocuments] = useState([]);
    const [message, setMessage] = useState('');

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSourceChange = (e) => {
        setSourceName(e.target.value);
    };

    const handleUpload = async (e) => {
        e.preventDefault();

        if (!file || !sourceName) {
            setMessage('Please provide both file and source name.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);
        formData.append('source_name', sourceName);

        try {
            // Upload document
            const uploadResponse = await axios.post(`/upload-document/${sourceName}/`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            if (uploadResponse.data.status === 'success') {
                setMessage('File uploaded successfully.');

                // Fetch documents
                const getDocumentsResponse = await axios.get(`/get-documents/${sourceName}/`);
                setDocuments(getDocumentsResponse.data.documents); // Adjust based on your response structure
            } else {
                setMessage(`Upload failed: ${uploadResponse.data.message}`);
            }
        } catch (error) {
            console.error('Error during upload or fetching documents:', error);
            setMessage('An error occurred during the upload or document fetching.');
        }
    };

    return (
        <div>
            <h2>Upload Document</h2>
            <form onSubmit={handleUpload}>
                <div>
                    <label>
                        Source Name:
                        <input type="text" value={sourceName} onChange={handleSourceChange} required />
                    </label>
                </div>
                <div>
                    <label>
                        Document:
                        <input type="file" onChange={handleFileChange} required />
                    </label>
                </div>
                <button type="submit">Upload</button>
            </form>
            {message && <p>{message}</p>}
            {documents.length > 0 && (
                <div>
                    <h3>Documents List:</h3>
                    <ul>
                        {documents.map((doc, index) => (
                            <li key={index}>{doc.file_name} - {doc.file_url}</li> // Adjust based on your document structure
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default UploadDocument;
