import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Header from './Header';
import ConversationBox from './ConversationBox';
import InputBox from './InputBox';

function Home() {
  const { mysource } = useParams(); // Retrieve 'mysource' from URL parameters
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [djangoResult, setDjangoResult] = useState(null);
  const [showSpinner, setShowSpinner] = useState(false);
  const [question, setQuestion] = useState('');
  const conversationBoxRef = useRef(null);

  // Scroll to the bottom of the conversation box when new messages are added
  useEffect(() => {
    if (conversationBoxRef.current) {
      conversationBoxRef.current.scrollTop = conversationBoxRef.current.scrollHeight;
    }
  }, [messages]);

  // Function to handle sending a message
  const handleSendMessage = async (e) => {
    e.preventDefault();
    const message = e.target.message.value.trim(); // Get the message from the form
    if (message !== '') {
      e.target.message.value = ''; // Clear the input field
      setIsLoading(true); // Set loading state to true
      setShowSpinner(true); // Show spinner
      setMessages([...messages, { text: message, sender: 'user' }]); // Add user message to state

      try {
        const payload = {
          question: message,
          source: mysource || 'defaultSource' // Use provided source or default
        };
        console.log('Sending payload:', payload); // Log the payload being sent
        const resp = await axios.post('/chatbot1/search/', payload); // Send POST request
        console.log('Full response from API:', resp.data); // Log full response

        let response = resp.data.response || "No response from API"; // Get response or default message

        console.log('Response from API:', response);

        setMessages((prevMessages) => [
          ...prevMessages,
          {
            text: response,
            sender: 'bot',
            copyButton: true,
            filenames: resp.data.references ? resp.data.references.map((ref) => ref.filename) : [], // Extract filenames if available
          },
        ]);

        setShowSpinner(false); // Hide spinner

        if (conversationBoxRef.current) {
          conversationBoxRef.current.scrollTop = conversationBoxRef.current.scrollHeight; // Scroll to the bottom
        }
      } catch (error) {
        console.error('Error sending message:', error); // Log error if request fails
      }

      setIsLoading(false); // Set loading state to false
      setShowSpinner(false); // Hide spinner
    }
  };

  return (
    <div className={`Home ${mysource === 'OADInternal' ? 'internal' : ''}`}>
      <Header djangoResult={djangoResult} />
      <h1>{mysource === 'OADPublic' ? 'Public Chatbot' : 'Internal Chatbot'}</h1>
      <ConversationBox
        messages={messages}
        isLoading={isLoading}
        conversationBoxRef={conversationBoxRef}
      />
      <InputBox
        question={question}
        setQuestion={setQuestion}
        handleSendMessage={handleSendMessage}
        isLoading={isLoading}
        showSpinner={showSpinner}
      />
    </div>
  );
}

export default Home;
