// ListSources.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useParams, useNavigate } from 'react-router-dom';

function ListSources() {
  const [globalSources, setGlobalSources] = useState([]);
  const [privateSources, setPrivateSources] = useState([]);
  const [selectedSource, setSelectedSource] = useState(null);
  const [documents, setDocuments] = useState(null);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const { source } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    fetchSources();
  }, []);

  useEffect(() => {
    if (source) {
      fetchDocuments(source);
    }
  }, [source]);

  const fetchSources = async () => {
    setIsLoading(true);
    setMessage('');

    try {
      const response = await axios.get('/api/v1/sources/', {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.status === 200) {
        setGlobalSources(response.data.global_sources || []);
        setPrivateSources(response.data.private_sources || []);
      } else {
        setMessage('An error occurred while fetching the sources.');
      }
    } catch (error) {
      console.error('Error during source fetching:', error);
      setMessage(error.response?.data?.error || 'An error occurred while fetching the sources.');
    } finally {
      setIsLoading(false);
    }
  };

  const fetchDocuments = async (sourceName) => {
    setIsLoading(true);
    setMessage('');
    setSelectedSource(sourceName);

    try {
      const response = await axios.get(`/api/v1/sources/${sourceName}/documents`, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.status === 200) {
        setDocuments(response.data);
        if (response.data.documents.length === 0) {
          setMessage('No documents found in this source.');
        }
      } else {
        setMessage('An error occurred while fetching the documents.');
      }
    } catch (error) {
      console.error('Error during document fetching:', error);
      setMessage(error.response?.data?.error || 'An error occurred while fetching the documents.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSyncSource = async () => {
    try {
      const response = await axios.post(`/api/v1/sources/${selectedSource}/sync`, {}, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });
      alert(response.data.message);
      fetchDocuments(selectedSource);
    } catch (error) {
      console.error('Error syncing source:', error);
      alert(error.response?.data?.error || 'An error occurred during synchronization.');
    }
  };

  function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.substring(0, name.length + 1) === (name + '=')) {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  }

  const renderSourceList = (sources, title) => (
    <div>
      <h2>{title}</h2>
      {sources.length > 0 ? (
        <ul>
          {sources.map((source) => (
            <li key={source}>
              <Link to={`/sources/${source}`}>{source}</Link>
            </li>
          ))}
        </ul>
      ) : (
        <p>No {title.toLowerCase()} found.</p>
      )}
    </div>
  );

  const renderDocuments = () => (
    <div>
      <h2>Documents in {selectedSource}</h2>
      {documents && documents.documents ? (
        <>
          <p>Total documents: {documents.documents.length}</p>
          <ul>
            {documents.documents.map((document, index) => (
              <li key={document.id || index}>
                <h4>{document.filename}</h4>
                {document.filepath && <p>Location: {document.filepath}</p>}
              </li>
            ))}
          </ul>
        </>
      ) : (
        <p>No documents found in this source.</p>
      )}
      <Link to={`/sources/${selectedSource}/upload`}>Upload Document</Link>
      <button onClick={() => navigate('/')}>Back to Sources</button>
      <button onClick={handleSyncSource}>Sync Source</button>
    </div>
  );

  return (
    <div>
      <h1>List of Sources</h1>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <>
          {selectedSource ? (
            renderDocuments()
          ) : (
            <>
              {renderSourceList(globalSources, 'Global Sources')}
              {renderSourceList(privateSources, 'Private Sources')}
            </>
          )}
        </>
      )}
      {message && <p>{message}</p>}
    </div>
  );
}

export default ListSources;
