import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Header from './Header';
import ConversationBox from './ConversationBox';
import InputBox from './InputBox';

function Home() {
  const { mysource } = useParams();
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [showSpinner, setShowSpinner] = useState(false);
  const [question, setQuestion] = useState('');
  const conversationBoxRef = useRef(null);

  useEffect(() => {
    if (conversationBoxRef.current) {
      conversationBoxRef.current.scrollTop = conversationBoxRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    const message = e.target.message.value.trim();
    if (message !== '') {
      e.target.message.value = '';
      setIsLoading(true);
      setShowSpinner(true);
      setMessages([...messages, { text: message, sender: 'user' }]);

      try {
        const payload = {
          question: message,
          source: mysource || 'defaultSource' // Ensure source is included
        };
        console.log('Sending payload:', payload); // Log the payload

        const resp = await axios.post('/chatbot1/search/', payload);
        console.log('Full response from API:', resp.data);

        let response = resp.data.response || "No response from API";
        console.log('Response from API:', response);

        setMessages((prevMessages) => [
          ...prevMessages,
          {
            text: response,
            sender: 'bot',
            copyButton: true,
            filenames: resp.data.references ? resp.data.references.map((ref) => ref.filename) : [],
          },
        ]);

        setShowSpinner(false);

        if (conversationBoxRef.current) {
          conversationBoxRef.current.scrollTop = conversationBoxRef.current.scrollHeight;
        }
      } catch (error) {
        console.error('Error sending message:', error);
      }

      setIsLoading(false);
      setShowSpinner(false);
    }
  };

  return (
    <div className={`Home ${mysource === 'OADInternal' ? 'internal' : ''}`}>
      <Header />
      <h1>{mysource === 'OADPublic' ? 'Public Chatbot' : 'Internal Chatbot'}</h1>
      <ConversationBox
        messages={messages}
        isLoading={isLoading}
        conversationBoxRef={conversationBoxRef}
      />
      <InputBox
        question={question}
        setQuestion={setQuestion}
        handleSendMessage={handleSendMessage}
        isLoading={isLoading}
        showSpinner={showSpinner}
      />
    </div>
  );
}

export default Home;
