// ChatWithSource.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ChatWithSource = () => {
    const { sourceName } = useParams();
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');

    useEffect(() => {
        // Fetch any initial data needed for the source
    }, [sourceName]);

    const sendMessage = async () => {
        if (!input) return;

        const newMessage = {
            text: input,
            user: 'Me',
        };
        setMessages([...messages, newMessage]);
        setInput('');

        try {
            const response = await axios.post(`/chat-with-source/${sourceName}/`, { message: input });  // Ensure URL ends with a slash
            setMessages([...messages, newMessage, { text: response.data.message, user: 'Bot' }]);
        } catch (error) {
            console.error('Error sending message:', error);
        }
    };

    return (
        <div>
            <h2>Chat with Source: {sourceName}</h2>
            <div>
                {messages.map((message, index) => (
                    <div key={index}>
                        <strong>{message.user}:</strong> {message.text}
                    </div>
                ))}
            </div>
            <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyPress={(e) => (e.key === 'Enter' ? sendMessage() : null)}
            />
            <button onClick={sendMessage}>Send</button>
        </div>
    );
};

export default ChatWithSource;
