import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function UploadDocument() {
  const [file, setFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const { source } = useParams();
  const navigate = useNavigate();

  useEffect(() => {

    const socket = new WebSocket(`ws://127.0.0.1:8001/ws/documents/${source}/`);

    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.message) {
        setUploadStatus(data.message.status);
        setProgress(data.message.progress);
        if (data.message.status === 'COMPLETE') {
          setTimeout(() => navigate(`/sources/${source}`), 2000);
        }
      }
    };

    return () => {
      socket.close();
    };
  }, [source, navigate]);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.size > 10 * 1024 * 1024) {
      alert('File is too large. Maximum size is 10MB.');
      e.target.value = null;
    } else {
      setFile(selectedFile);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      alert('Please select a file to upload');
      return;
    }

    setIsLoading(true);
    setUploadStatus('Initiating upload...');
    setProgress(10);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post(`/upload-document/${source}/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true,
      });

      if (response.data.status === 'success') {
        setUploadStatus('File upload initiated. Processing...');
      } else {
        setUploadStatus('Unexpected response. Please check the server logs.');
        setProgress(0);
      }
    } catch (error) {
      console.error('Error uploading document:', error);
      setUploadStatus('Error: ' + (error.response?.data?.message || error.message || 'Unknown error occurred'));
      setProgress(0);
    } finally {
      setIsLoading(false);
    }
  };

  function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.substring(0, name.length + 1) === (name + '=')) {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  }

  return (
    <div>
      <h2>Upload Document to {source}</h2>
      <form onSubmit={handleUpload}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit" disabled={isLoading}>Upload</button>
      </form>
      <div>
        <p>Status: {uploadStatus}</p>
        <p>Progress: {progress}%</p>
      </div>
    </div>
  );
}

export default UploadDocument;
