import React, { useState } from 'react';
import axios from 'axios';

function CreateSource() {
  const [sourceName, setSourceName] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('/chatbot1/create-source/', {
        source: sourceName,
        description: description
      });

      if (response.status === 201) {
        setMessage('Source successfully created!');
      } else {
        setMessage('An error occurred while creating the source.');
      }
    } catch (error) {
      console.error('Error during source creation:', error);
      setMessage('An error occurred while creating the source.');
    }
  };

  return (
    <div>
      <h1>Create a New Source</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Source Name:</label>
          <input
            type="text"
            value={sourceName}
            onChange={(e) => setSourceName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Description:</label>
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <button type="submit">Create Source</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default CreateSource;
