// src/SAMLAuth.js

import axios from 'axios';

const BASE_URL = 'http://localhost:8000'; // Replace with your Django backend URL

export const SAMLAuth = {
  login: () => {
    window.location.href = `${BASE_URL}/saml2/login/`;
  },

  logout: () => {
    return axios.get(`${BASE_URL}/saml2/logout/`, { withCredentials: true })
      .then(() => {
        // Clear any local storage or state here
        localStorage.removeItem('user');
      });
  },

  getCurrentUser: () => {
    return axios.get(`${BASE_URL}/api/current-user/`, { withCredentials: true })
      .then(response => {
        if (response.data.authenticated) {
          localStorage.setItem('user', JSON.stringify(response.data.user));
          return response.data.user;
        }
        return null;
      })
      .catch(() => {
        return null;
      });
  },

  isAuthenticated: () => {
    return localStorage.getItem('user') !== null;
  }
};
