import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ListSources() {
  const [sources, setSources] = useState([]);
  const [selectedSource, setSelectedSource] = useState(null);
  const [documents, setDocuments] = useState([]);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [sortField, setSortField] = useState('name');
  const [sortDirection, setSortDirection] = useState('asc');

  useEffect(() => {
    fetchSources();
  }, []);

  const fetchSources = async () => {
    setIsLoading(true);
    setMessage('');
    try {
      const response = await axios.get('/chatbot1/list-sources/', {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.status === 200) {
        setSources(response.data.external_sources.global || []);
      } else {
        setMessage('An error occurred while fetching the sources.');
      }
    } catch (error) {
      console.error('Error during source fetching:', error);
      setMessage(error.response?.data?.error || 'An error occurred while fetching the sources.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSort = (field) => {
    if (field === sortField) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('asc');
    }
  };

  const sortSources = (sourcesToSort) => {
    return sourcesToSort.sort((a, b) => {
      let comparison = 0;
      if (a[sortField] < b[sortField]) {
        comparison = -1;
      } else if (a[sortField] > b[sortField]) {
        comparison = 1;
      }
      return sortDirection === 'asc' ? comparison : -comparison;
    });
  };

  const formatDate = (dateString) => {
    if (dateString) {
      return new Date(dateString).toLocaleString();
    }
    return 'N/A';
  };

  const renderSourcesTable = () => (
    <div style={{ backgroundColor: 'white', padding: '20px' }}>
      <h2 style={{ color: '#444', marginTop: '20px' }}>Sources</h2>
      {sources.length > 0 ? (
        <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
          <thead>
            <tr style={{ backgroundColor: '#f8f9fa' }}>
              <th style={tableHeaderStyle} onClick={() => handleSort('name')}>
                Source Name {sortField === 'name' && (sortDirection === 'asc' ? '▲' : '▼')}
              </th>
              <th style={tableHeaderStyle} onClick={() => handleSort('visibility')}>
                Visibility {sortField === 'visibility' && (sortDirection === 'asc' ? '▲' : '▼')}
              </th>
              <th style={tableHeaderStyle} onClick={() => handleSort('model')}>
                Model {sortField === 'model' && (sortDirection === 'asc' ? '▲' : '▼')}
              </th>
              <th style={tableHeaderStyle} onClick={() => handleSort('created_at')}>
                Created At {sortField === 'created_at' && (sortDirection === 'asc' ? '▲' : '▼')}
              </th>
              <th style={tableHeaderStyle} onClick={() => handleSort('updated_at')}>
                Updated At {sortField === 'updated_at' && (sortDirection === 'asc' ? '▲' : '▼')}
              </th>
              <th style={tableHeaderStyle}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {sortSources(sources).map((source, index) => (
              <tr key={source.id || index}>
                <td style={tableCellStyle}>{source.name}</td>
                <td style={tableCellStyle}>{source.visibility}</td>
                <td style={tableCellStyle}>{source.model || 'N/A'}</td>
                <td style={tableCellStyle}>{formatDate(source.created_at)}</td>
                <td style={tableCellStyle}>{formatDate(source.updated_at)}</td>
                <td style={tableCellStyle}>
                  <button onClick={() => fetchDocuments(source.name)} style={buttonStyle}>
                    View Documents
                  </button>
                  <button onClick={() => handleSyncSource(source.name)} style={buttonStyle}>
                    Sync Source
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No sources available.</p>
      )}
    </div>
  );

  const getCookie = (name) => {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.substring(0, name.length + 1) === `${name}=`) {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  };

  const handleSyncSource = async (sourceName) => {
    try {
      const response = await axios.post(`/chatbot1/sync-source/${sourceName}/`, {}, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });
      alert(response.data.message);
      fetchSources();
    } catch (error) {
      console.error('Error syncing source:', error);
      alert(error.response?.data?.error || 'An error occurred during synchronization.');
    }
  };

  const fetchDocuments = async (sourceName) => {
    setIsLoading(true);
    setMessage('');
    setSelectedSource(sourceName);

    try {
      const response = await axios.get(`/chatbot1/list-documents/${sourceName}/`, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.status === 200) {
        setDocuments(response.data.documents.documents);
        setMessage(response.data.documents.documents.length === 0 ? 'No documents found.' : '');
      } else {
        setMessage('An error occurred while fetching the documents.');
      }
    } catch (error) {
      console.error('Error during document fetching:', error);
      setMessage(error.response?.data?.error || 'An error occurred while fetching the documents.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      {isLoading && <p>Loading...</p>}
      {message && <p>{message}</p>}
      {renderSourcesTable()}
    </div>
  );
}

const tableHeaderStyle = {
  padding: '10px',
  border: '1px solid #ddd',
  backgroundColor: '#f8f9fa',
  cursor: 'pointer',
};

const tableCellStyle = {
  padding: '10px',
  border: '1px solid #ddd',
};

const buttonStyle = {
  padding: '5px 10px',
  margin: '5px',
  backgroundColor: '#007bff',
  color: 'white',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
};

export default ListSources;
