// ConversationBox.js
import React from 'react';
import CopyButton from './CopyButton';

function createMarkup(html) {
  return { __html: html };
}

function Message({ message, isLoading, index, messages }) {
  return (
    <div className={`message ${message.sender === 'user' ? 'user' : 'bot'}`}>
      {message.text.trim().split('\n\n').map((line, i) => (
        <p key={i} dangerouslySetInnerHTML={createMarkup(line.trim())}></p>
      ))}
      {message.sender === 'bot' && message.copyButton && (
        <div>
          {message.filenames && message.filenames.length > 0 && (
            <p className="filenames">
              Found in: {message.filenames.join(', ')}
            </p>
          )}
          <CopyButton text={message.text} />
        </div>
      )}
      {isLoading && index === messages.length - 1 && (
        <div className="spinner spinner-conversation"></div>
      )}
    </div>
  );
}

function ConversationBox({ messages, isLoading, conversationBoxRef }) {
  console.log('Messages:', messages);
  return (
    <div className="conversation-box" ref={conversationBoxRef}>
      {messages.map((message, index) => (
        <Message key={index} message={message} isLoading={isLoading} index={index} messages={messages} />
      ))}
    </div>
  );
}

export default ConversationBox;
