import React from 'react';
import { useParams } from 'react-router-dom';
import './App.css'; // Import the main CSS file
import './app-public.css'; // Import public-specific styles

function Chatbot() {
  const { mysource } = useParams();

  // Define styles based on the `mysource` parameter
  const containerStyle = {
    backgroundColor: mysource === 'public' ? 'lightblue' : 'lightcoral',
    width: '100%',
    height: '100%',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  };

  return (
    <div style={containerStyle}>
      <iframe src={`/OAD?mysource=${mysource}`} style={{ width: '100%', height: '100%', border: 'none' }} />
    </div>
  );
}

export default Chatbot;
