// ListSources.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ListSources() {
  const [sources, setSources] = useState([]);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchSources();
  }, []);

  const fetchSources = async () => {
    setIsLoading(true);
    setMessage('');

    try {
      const response = await axios.get('/chatbot1/list-sources/', {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.status === 200) {
        setSources(response.data);
      } else {
        setMessage('An error occurred while fetching the sources.');
      }
    } catch (error) {
      console.error('Error during source fetching:', error);
      setMessage(error.response?.data?.error || 'An error occurred while fetching the sources.');
    } finally {
      setIsLoading(false);
    }
  };

  // Function to get CSRF token from cookies
  function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.substring(0, name.length + 1) === (name + '=')) {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  }

  return (
    <div style={{ padding: '20px', backgroundColor: 'white', maxWidth: '800px', margin: '0 auto' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>List of Sources</h1>
      {isLoading ? (
        <p style={{ textAlign: 'center' }}>Loading sources...</p>
      ) : (
        <>
          {sources.length > 0 ? (
            <ul style={{ listStyleType: 'none', padding: 0 }}>
              {sources.map((source) => (
                <li key={source.source} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px', borderRadius: '4px' }}>
                  <h3 style={{ margin: '0 0 10px 0' }}>{source.source}</h3>
                  <p style={{ margin: '0' }}><strong>Description:</strong> {source.description}</p>
                  <p style={{ margin: '5px 0' }}><strong>Created:</strong> {new Date(source.created).toLocaleString()}</p>
                  <p style={{ margin: '0' }}><strong>Created by:</strong> {source.created_by}</p>
                </li>
              ))}
            </ul>
          ) : (
            <p style={{ textAlign: 'center' }}>No sources found.</p>
          )}
        </>
      )}
      {message && <p style={{ marginTop: '15px', textAlign: 'center', color: 'red' }}>{message}</p>}
    </div>
  );
}

export default ListSources;
