import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UploadDocument = () => {
    const [file, setFile] = useState(null);
    const [sourceName, setSourceName] = useState('');
    const [documents, setDocuments] = useState([]);
    const [message, setMessage] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSourceChange = (e) => {
        setSourceName(e.target.value);
    };

    const fetchDocuments = async () => {
        try {
            const response = await axios.get(`/chatbot1/list-documents/${sourceName}/`);
            if (response.data && response.data.documents) {
                setDocuments(response.data.documents);
            } else {
                console.error('Unexpected response structure:', response.data);
            }
        } catch (error) {
            console.error('Error fetching documents:', error);
            setMessage('Failed to fetch documents.');
        }
    };

    const handleUpload = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        if (!file || !sourceName) {
            setMessage('Please provide both file and source name.');
            setIsLoading(false);
            return;
        }

        const formData = new FormData();
        formData.append('file', file);

        try {
            const uploadResponse = await axios.post(`/chatbot1/upload-document/${sourceName}/`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    'X-CSRFToken': getCookie('csrftoken'),
                },
                withCredentials: true
            });

            if (uploadResponse.data.status === 'success') {
                setMessage('File uploaded successfully.');
                await fetchDocuments();
            } else {
                setMessage(`Upload failed: ${uploadResponse.data.message}`);
            }
        } catch (error) {
            console.error('Error during upload:', error);
            setMessage('An error occurred during the upload.');
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        if (sourceName) {
            fetchDocuments();
        }
    }, [sourceName]);

    const getCookie = (name) => {
        let cookieValue = null;
        if (document.cookie && document.cookie !== '') {
            const cookies = document.cookie.split(';');
            for (let i = 0; i < cookies.length; i++) {
                const cookie = cookies[i].trim();
                if (cookie.substring(0, name.length + 1) === `${name}=`) {
                    cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                    break;
                }
            }
        }
        return cookieValue;
    };

    return (
        <div>
            <h2>Upload Document</h2>
            <form onSubmit={handleUpload}>
                <div>
                    <label>
                        Source Name:
                        <input type="text" value={sourceName} onChange={handleSourceChange} required />
                    </label>
                </div>
                <div>
                    <label>
                        Document:
                        <input type="file" onChange={handleFileChange} required />
                    </label>
                </div>
                <button type="submit" disabled={isLoading}>
                    {isLoading ? 'Uploading...' : 'Upload'}
                </button>
            </form>
            {message && <p>{message}</p>}
            {documents.length > 0 && (
                <div>
                    <h3>Documents List:</h3>
                    <ul>
                        {documents.map((doc, index) => (
                            <li key={index}>{doc.filename} - {doc.filepath}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default UploadDocument;
