// src/CreateSource.js
import React, { useState } from 'react';
import axios from 'axios';

function CreateSource() {
  const [sourceName, setSourceName] = useState('');
  const [description, setDescription] = useState('');
  const [responseMessage, setResponseMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const resp = await axios.post('/chatbot1/create-source/', {
        source: sourceName,
        description: description || 'OUS Agile DevOps - Ask a source',
      });

      setResponseMessage('Source created successfully!');
    } catch (error) {
      console.error('Error creating source:', error);
      setResponseMessage('Failed to create source.');
    }
  };

  return (
    <div>
      <h2>Create a New Source</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Source Name:</label>
          <input
            type="text"
            value={sourceName}
            onChange={(e) => setSourceName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Description (optional):</label>
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <button type="submit">Create Source</button>
      </form>
      {responseMessage && <p>{responseMessage}</p>}
    </div>
  );
}

export default CreateSource;
