import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import CopyButton from './CopyButton';

function App() {
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [djangoResult, setDjangoResult] = useState(null);
  const [showSpinner, setShowSpinner] = useState(false);
  const [question, setQuestion] = useState('');
  const [response, setResponse] = useState('');
  const [error, setError] = useState(null);
  const conversationBoxRef = useRef(null);

  useEffect(() => {
    if (conversationBoxRef.current) {
      conversationBoxRef.current.scrollTop = conversationBoxRef.current.scrollHeight;
    }

    // Call Django view and display answer when the component mounts
    const fetchDjangoResult = async () => {
      const djangoUrl = '/add-one/'; // Update with the correct Django URL
      const data = { number: 5 }; // Replace with your desired data

      try {
        const djangoResp = await axios.post(djangoUrl, data);
        const djangoResult = djangoResp.data.result;
        setDjangoResult(djangoResult);
      } catch (error) {
        console.error(error);
      }
    };

    fetchDjangoResult();
  }, []);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    const message = e.target.message.value.trim();
    if (message !== '') {
      e.target.message.value = '';
      setIsLoading(true);
      setShowSpinner(true);
      setMessages([...messages, { text: message, sender: 'user' }]);

      // Augment messages array with user messages
      const updatedMessages = [...messages];
      updatedMessages.push({ text: message, sender: 'user' });

      try {
        // Perform REST API call to the Django backend
        const resp = await axios.post('/chatbot1/search/', { question: message });
        let response = resp.data.response;

        // Clean API response
        response = response.replace(/\[(.*?)\]/g, '\n\n').replace(/(\w+)\.\n\n/g, '$1\n');
        response = response.replace(/\.\n\n/g, '.\n');
        response = response.replace(/^\s+|\s+$/g, '').replace(/^"|"$/g, ''); // Remove leading and trailing spaces and opening/closing double quotes
        response = response.replace(/\.\s+(\d+)\./g, '$1.');
        response = response.replace(/^\.\s*/gm, '');
        response = response.replace(/([^.\n])$/gm, '.');

        const linkRegex = /(https?:\/\/[^\s]+)/g;
        response = response.replace(linkRegex, (match, url) => {
          return `<a href="${url}" target="_blank" rel="noopener noreferrer">${url}</a>`;
        });

        const urlRegex = /go\/([^\s]+)/g;
        response = response.replace(urlRegex, (match, sometext) => {
          const href = `http://go/${sometext}`;
          return `<a href="${href}" target="_blank" rel="noopener noreferrer">go/${sometext}</a>`;
        });

        const emailRegex = /([a-zA-Z0-9._-]+@[a-zA-Z0-9._-]+\.[a-zA-Z0-9_-]+)/g;
        response = response.replace(emailRegex, (match, email) => {
          const href = `mailto:${email}`;
          return `<a href="${href}">${email}</a>`;
        });

        setMessages((prevMessages) => [
          ...prevMessages,
          {
            text: response,
            sender: 'bot',
            copyButton: true,
            filenames: resp.data.references ? resp.data.references.map((ref) => ref.filename) : [], // Add filenames property
          },
        ]);

        setShowSpinner(false); // Hide the spinner

        if (conversationBoxRef.current) {
          conversationBoxRef.current.scrollTop = conversationBoxRef.current.scrollHeight;
        }
      } catch (error) {
        console.error(error);
      }

      setIsLoading(false);
      setShowSpinner(false); // Hide the spinner
      e.target.message.value = '';
    }
  };

  const createMarkup = (html) => {
    return { __html: html };
  };

  useEffect(() => {
    if (conversationBoxRef.current) {
      conversationBoxRef.current.scrollTop = conversationBoxRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="App">
      <header>
        <img src={process.env.PUBLIC_URL + '/logo.jpg'} alt="Logo" className="logo" />{djangoResult}
      </header>
      <div className="conversation-box" ref={conversationBoxRef}>
        {messages.map((message, index) => (
          <div
            key={index}
            className={`message ${message.sender === 'user' ? 'user' : 'bot'}`}
          >
            {message.text.trim().split('\n\n').map((line, index) => (
              <p
                key={index}
                dangerouslySetInnerHTML={createMarkup(line.trim())}
              ></p>
            ))}
            {message.sender === 'bot' && message.copyButton && (
              <div>
                {message.filenames && message.filenames.length > 0 && (
                  <p className="filenames">
                    Found in: {message.filenames.join(', ')}
                  </p>
                )}
                <CopyButton text={message.text} />
              </div>
            )}
            {isLoading && index === messages.length - 1 && (
              <div className="spinner spinner-conversation"></div>
            )}
          </div>
        ))}
      </div>
      <form className="input-box" onSubmit={handleSendMessage}>
        <div className="input-container">
          <input
            type="text"
            name="message"
            placeholder="Ask OUS Agile DevOps.."
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            disabled={isLoading}
          />
          <button className="bot-button" type="submit" disabled={isLoading}>
            {isLoading ? (
              <div className="spinner spinner-button"></div>
            ) : (
              <div className="spinner spinner-button" style={{ display: showSpinner ? 'block' : 'none' }}></div>
            )}
            Send
          </button>
        </div>
      </form>
    </div>
  );
}

export default App;
