import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UploadDocument = () => {
    const [file, setFile] = useState(null);
    const [sourceName, setSourceName] = useState('');
    const [documents, setDocuments] = useState([]);
    const [message, setMessage] = useState('');

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSourceChange = (e) => {
        setSourceName(e.target.value);
    };

    const fetchDocuments = async () => {
        try {
            const response = await axios.get(`/get-documents/${sourceName}/`);
            if (response.data && response.data.documents) {
                setDocuments(response.data.documents);
            } else {
                console.error('Unexpected response structure:', response.data);
            }
        } catch (error) {
            console.error('Error fetching documents:', error);
            setMessage('Failed to fetch documents.');
        }
    };

    const handleUpload = async (e) => {
        e.preventDefault();

        if (!file || !sourceName) {
            setMessage('Please provide both file and source name.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);
        formData.append('source_name', sourceName);

        try {
            const uploadResponse = await axios.post(`/upload-document/${sourceName}/`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            if (uploadResponse.data.status === 'success') {
                setMessage('File uploaded successfully.');
                await fetchDocuments();
            } else {
                setMessage(`Upload failed: ${uploadResponse.data.message}`);
            }
        } catch (error) {
            console.error('Error during upload:', error);
            setMessage('An error occurred during the upload.');
        }
    };

    useEffect(() => {
        if (sourceName) {
            fetchDocuments();
        }
    }, [sourceName]);

    return (
        <div>
            <h2>Upload Document</h2>
            <form onSubmit={handleUpload}>
                <div>
                    <label>
                        Source Name:
                        <input type="text" value={sourceName} onChange={handleSourceChange} required />
                    </label>
                </div>
                <div>
                    <label>
                        Document:
                        <input type="file" onChange={handleFileChange} required />
                    </label>
                </div>
                <button type="submit">Upload</button>
            </form>
            {message && <p>{message}</p>}
            {documents.length > 0 && (
                <div>
                    <h3>Documents List:</h3>
                    <ul>
                        {documents.map((doc, index) => (
                            <li key={index}>{doc.file_name} - {doc.file_url}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default UploadDocument;
