import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ListSources() {
  const [sources, setSources] = useState([]);
  const [selectedSource, setSelectedSource] = useState(null);
  const [documents, setDocuments] = useState(null);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadTaskId, setUploadTaskId] = useState(null);

  useEffect(() => {
    fetchSources();
  }, []);

  const fetchSources = async () => {
    setIsLoading(true);
    setMessage('');

    try {
      const response = await axios.get('/chatbot1/list-sources/', {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      setSources(response.data);
    } catch (error) {
      console.error('Error fetching sources:', error);
      setMessage('An error occurred while fetching the sources.');
    } finally {
      setIsLoading(false);
    }
  };

  const fetchDocuments = async (source) => {
    setIsLoading(true);
    setMessage('');
    setSelectedSource(source);

    try {
      const response = await axios.get(`/chatbot1/list-documents/${source}/`, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      setDocuments(response.data.documents);
      if (response.data.documents.count === 0) {
        setMessage('No documents found.');
      }
    } catch (error) {
      console.error('Error fetching documents:', error);
      setMessage('An error occurred while fetching the documents.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setMessage('Please select a file to upload.');
      return;
    }

    setIsLoading(true);
    setUploadProgress(0);
    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post(`/chatbot1/upload-document/${selectedSource}/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true,
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setUploadProgress(percentCompleted);
        }
      });

      setUploadTaskId(response.data.task_id);
      setMessage('Document upload initiated. Checking status...');
      checkUploadStatus(response.data.task_id);
    } catch (error) {
      console.error('Error uploading document:', error);
      setMessage(error.response?.data?.error || 'An error occurred while uploading the document.');
    } finally {
      setIsLoading(false);
      setUploadProgress(0);
    }
  };

  const checkUploadStatus = async (taskId) => {
    try {
      const response = await axios.get(`/chatbot1/check-upload-status/${selectedSource}/${taskId}/`, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.data.status === 'completed') {
        setMessage('Document uploaded successfully.');
        setSelectedFile(null);
        fetchDocuments(selectedSource);
      } else if (response.data.status === 'failed') {
        setMessage('Document upload failed.');
      } else {
        setTimeout(() => checkUploadStatus(taskId), 5000); // Check again after 5 seconds
      }
    } catch (error) {
      console.error('Error checking upload status:', error);
      setMessage('An error occurred while checking the upload status.');
    }
  };

  function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.substring(0, name.length + 1) === (name + '=')) {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  }

  const renderSourceList = () => (
    <div>
      <h2 style={{ color: '#444', marginTop: '20px' }}>Sources</h2>
      {sources.length > 0 ? (
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {sources.map((source) => (
            <li key={source} style={{ marginBottom: '10px', border: '1px solid #ccc', padding: '10px', borderRadius: '4px' }}>
              <h3 style={{ margin: '0' }}>
                <a href="#" onClick={(e) => { e.preventDefault(); fetchDocuments(source); }} style={{ color: '#007bff', textDecoration: 'none' }}>
                  {source}
                </a>
              </h3>
            </li>
          ))}
        </ul>
      ) : (
        <p>No sources found.</p>
      )}
    </div>
  );

  const renderDocuments = () => (
    <div>
      <h2 style={{ color: '#444', marginTop: '20px' }}>Documents in {selectedSource}</h2>
      {documents ? (
        <>
          <p>Total documents: {documents.count}</p>
          {documents.documents.length > 0 ? (
            <ul style={{ listStyleType: 'none', padding: 0 }}>
              {documents.documents.map((document, index) => (
                <li key={document.id || index} style={{ marginBottom: '10px', border: '1px solid #ccc', padding: '10px', borderRadius: '4px' }}>
                  <h4 style={{ margin: '0' }}>{document.filename}</h4>
                  {document.filepath && (
                    <p style={{ margin: '5px 0 0', fontSize: '0.9em', color: '#666' }}>
                      Location: {document.filepath}
                    </p>
                  )}
                </li>
              ))}
            </ul>
          ) : (
            <p>No documents available in this source.</p>
          )}
        </>
      ) : (
        <p>No documents found in this source.</p>
      )}

      <div style={{ marginTop: '20px' }}>
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleUpload} style={{ marginLeft: '10px', padding: '10px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
          Upload Document
        </button>
      </div>

      {uploadProgress > 0 && uploadProgress < 100 && (
        <div style={{ marginTop: '10px' }}>
          <progress value={uploadProgress} max="100"></progress>
          <span>{uploadProgress}%</span>
        </div>
      )}

      <button onClick={() => setSelectedSource(null)} style={{ marginTop: '20px', padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
        Back to Sources
      </button>
    </div>
  );

  return (
    <div style={{ padding: '20px', backgroundColor: 'white', maxWidth: '800px', margin: '0 auto' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>List of Sources</h1>
      {isLoading ? (
        <p style={{ textAlign: 'center' }}>Loading...</p>
      ) : (
        <>
          {selectedSource ? renderDocuments() : renderSourceList()}
        </>
      )}
      {message && <p style={{ marginTop: '20px', color: 'red' }}>{message}</p>}
    </div>
  );
}

export default ListSources;
