// src/SAMLAuth.js

import axios from 'axios';

const BASE_URL = 'http://localhost:8000'; // Your Django backend URL

export const SAMLAuth = {
  login: () => {
    window.location.href = `${BASE_URL}/saml2/login/`;
  },

  logout: () => {
    return axios.get(`${BASE_URL}/saml2/logout/`, { withCredentials: true })
      .then(() => {
        localStorage.removeItem('user');
      })
      .catch(error => {
        console.error('Logout error:', error);
        throw error;
      });
  },

  getCurrentUser: () => {
    return axios.get(`${BASE_URL}/user-info/`, { withCredentials: true })
      .then(response => {
        if (response.data) {
          localStorage.setItem('user', JSON.stringify(response.data));
          return response.data;
        }
        return null;
      })
      .catch(error => {
        console.error('Get current user error:', error);
        return null;
      });
  },

  isAuthenticated: () => {
    return localStorage.getItem('user') !== null;
  }
};
