// ListSources.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ListSources() {
  const [globalSources, setGlobalSources] = useState([]);
  const [privateSources, setPrivateSources] = useState([]);
  const [selectedSource, setSelectedSource] = useState(null);
  const [documents, setDocuments] = useState(null);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [file, setFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState(null);
  const [chunkSize, setChunkSize] = useState('');
  const [chunkOverlap, setChunkOverlap] = useState('');
  const [separators, setSeparators] = useState('');
  const [splitPages, setSplitPages] = useState(false);
  const [customFields, setCustomFields] = useState('');

  useEffect(() => {
    fetchSources();
  }, []);

  const fetchSources = async () => {
    setIsLoading(true);
    setMessage('');

    try {
      const response = await axios.get('/chatbot1/list-sources/', {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });

      if (response.status === 200) {
        setGlobalSources(response.data.global_sources || []);
        setPrivateSources(response.data.private_sources || []);
      } else {
        setMessage('An error occurred while fetching the sources.');
      }
    } catch (error) {
      console.error('Error during source fetching:', error);
      setMessage(error.response?.data?.error || 'An error occurred while fetching the sources.');
    } finally {
      setIsLoading(false);
    }
  };

  const fetchDocuments = async (source) => {
  setIsLoading(true);
  setMessage('');
  setSelectedSource(source);

  try {
    const response = await axios.get(`/chatbot1/list-documents/${source}/`, {
      headers: {
        'Content-Type': 'application/json',
        'X-CSRFToken': getCookie('csrftoken'),
      },
      withCredentials: true
    });

    console.log('Fetch documents response:', response.data);

    if (response.status === 200) {
      setDocuments(response.data);
      if (response.data.documents.count === 0) {
        setMessage('No documents found. If you just uploaded a document, it may still be processing. Please check again in a few moments.');
      }
    } else {
      setMessage('An error occurred while fetching the documents.');
    }
  } catch (error) {
    console.error('Error during document fetching:', error);
    setMessage(error.response?.data?.error || 'An error occurred while fetching the documents.');
  } finally {
    setIsLoading(false);
  }
};

  function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.substring(0, name.length + 1) === (name + '=')) {
          cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
          break;
        }
      }
    }
    return cookieValue;
  }

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.size > 10 * 1024 * 1024) {
      alert('File is too large. Maximum size is 10MB.');
      e.target.value = null;
    } else {
      setFile(selectedFile);
    }
  };

  const handleUpload = async (e) => {
  e.preventDefault();
  if (!file) {
    alert('Please select a file to upload');
    return;
  }

  setIsLoading(true);
  setUploadStatus('Initiating upload...');
  const formData = new FormData();
  formData.append('file', file);
  if (chunkSize) formData.append('chunk_size', chunkSize);
  if (chunkOverlap) formData.append('chunk_overlap', chunkOverlap);
  if (separators) formData.append('separators', separators);
  formData.append('split_pages', splitPages);
  if (customFields) formData.append('custom_fields', customFields);

  try {
    const response = await axios.post(`/api/v1/sources/${selectedSource}/documents`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
        'X-CSRFToken': getCookie('csrftoken'),
      },
      withCredentials: true,
    });

    console.log('Upload response:', response.data);

    if (response.status === 202) {
      setUploadStatus('Upload accepted and is being processed.');
      if (response.data && response.data.task_id) {
        checkUploadStatus(selectedSource, response.data.task_id);
      } else {
        // If no task_id is provided, start polling for document appearance
        pollForDocument(selectedSource, file.name);
      }
    } else if (response.status === 201) {
      setUploadStatus('Upload completed successfully. Refreshing document list...');
      await fetchDocuments(selectedSource);
    } else {
      setUploadStatus('Unexpected response. Please check the server logs.');
    }
  } catch (error) {
    console.error('Error uploading document:', error);
    setUploadStatus('Error: ' + (error.response?.data?.detail || error.message || 'Unknown error occurred'));
  } finally {
    setIsLoading(false);
  }
};

  const pollForDocument = async (source, fileName, attempts = 0) => {
  if (attempts > 12) { // Stop after 1 minute (12 * 5 seconds)
    setUploadStatus('Document processing is taking longer than expected. Please check back later.');
    return;
  }

  try {
    const response = await axios.get(`/chatbot1/list-documents/${source}/`, {
      headers: {
        'Content-Type': 'application/json',
        'X-CSRFToken': getCookie('csrftoken'),
      },
      withCredentials: true
    });

    if (response.status === 200) {
      const documents = response.data.documents.documents;
      if (documents.some(doc => doc.filename === fileName)) {
        setUploadStatus('Document processed successfully. Refreshing document list...');
        await fetchDocuments(source);
      } else {
        setUploadStatus(`Processing document... (Attempt ${attempts + 1})`);
        setTimeout(() => pollForDocument(source, fileName, attempts + 1), 5000);
      }
    } else {
      setUploadStatus('Error checking document status. Please refresh the page.');
    }
  } catch (error) {
    console.error('Error polling for document:', error);
    setUploadStatus('Error checking document status. Please refresh the page.');
  }
};
  const checkUploadStatus = async (source, taskId) => {
  const url = `/api/v1/sources/${source}/documents/${taskId}/status`;
  console.log('Requesting URL:', url);
  console.log('Source:', source);
  console.log('Task ID:', taskId);

  try {
    const response = await axios.get(url);
    console.log('Response:', response);
    return response.data;
  } catch (error) {
    console.error('Error checking upload status:', error);
    if (error.response) {
      console.error('Response data:', error.response.data);
      console.error('Response status:', error.response.status);
      console.error('Response headers:', error.response.headers);
    } else if (error.request) {
      console.error('No response received:', error.request);
    } else {
      console.error('Error setting up request:', error.message);
    }
    throw error;
  }
};


  const handleSyncSource = async () => {
    try {
      const response = await axios.post(`/chatbot1/sync-source/${selectedSource}/`, {}, {
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'),
        },
        withCredentials: true
      });
      alert(response.data.message);
      fetchDocuments(selectedSource);
    } catch (error) {
      console.error('Error syncing source:', error);
      console.error('Error details:', error.response?.data);
      alert(error.response?.data?.error || 'An error occurred during synchronization. Please check the server logs for more details.');
    }
  };

  const renderSourceList = (sources, title) => (
    <div>
      <h2 style={{ color: '#444', marginTop: '20px' }}>{title}</h2>
      {sources.length > 0 ? (
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {sources.map((source) => (
            <li key={source} style={{ marginBottom: '10px', border: '1px solid #ccc', padding: '10px', borderRadius: '4px' }}>
              <h3 style={{ margin: '0' }}>
                <a href="#" onClick={(e) => { e.preventDefault(); fetchDocuments(source); }} style={{ color: '#007bff', textDecoration: 'none' }}>
                  {source}
                </a>
              </h3>
            </li>
          ))}
        </ul>
      ) : (
        <p>No {title.toLowerCase()} found.</p>
      )}
    </div>
  );

  const renderDocuments = () => {
  return (
    <div>
      <h2 style={{ color: '#444', marginTop: '20px' }}>Documents in {selectedSource}</h2>
      {documents && documents.documents ? (
        <>
          <p>Total documents: {documents.documents.count}</p>
          {Array.isArray(documents.documents.documents) && documents.documents.documents.length > 0 ? (
            <ul style={{ listStyleType: 'none', padding: 0 }}>
              {documents.documents.documents.map((document, index) => (
                <li key={document.id || index} style={{ marginBottom: '10px', border: '1px solid #ccc', padding: '10px', borderRadius: '4px' }}>
                  <h4 style={{ margin: '0' }}>{document.filename}</h4>
                  {document.filepath && (
                    <p style={{ margin: '5px 0 0', fontSize: '0.9em', color: '#666' }}>
                      Location: {document.filepath}
                    </p>
                  )}
                </li>
              ))}
            </ul>
          ) : (
            <p>No documents available in this source. If you just uploaded a document, it may still be processing.</p>
          )}
        </>
      ) : (
        <p>No documents found in this source. If you just uploaded a document, it may still be processing.</p>
      )}


        <button onClick={() => setSelectedSource(null)} style={{ marginTop: '20px', padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer', marginRight: '10px' }}>
          Back to Sources
        </button>
        <button onClick={handleSyncSource} style={{ marginTop: '20px', padding: '10px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
          Sync Source
        </button>

        <h3 style={{ marginTop: '20px' }}>Add Document</h3>
        <form onSubmit={handleUpload}>
          <input type="file" onChange={handleFileChange} />
          <label>
            <input type="checkbox" checked={splitPages} onChange={(e) => setSplitPages(e.target.checked)} />
            Split Pages
          </label>
          <textarea placeholder="Custom Fields (JSON)" value={customFields} onChange={(e) => setCustomFields(e.target.value)} />
          <button type="submit" disabled={isLoading}>
            {isLoading ? 'Uploading...' : 'Upload'}
          </button>
        </form>
        {uploadStatus && <p>{uploadStatus}</p>}
      </div>
    );
  };

  return (
    <div style={{ padding: '20px', backgroundColor: 'white', maxWidth: '800px', margin: '0 auto' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>List of Sources</h1>
      {isLoading ? (
        <p style={{ textAlign: 'center' }}>Loading...</p>
      ) : (
        <>
          {selectedSource ? (
            renderDocuments()
          ) : (
            <>
              {renderSourceList(globalSources, 'Global Sources')}
              {renderSourceList(privateSources, 'Private Sources')}
              {globalSources.length === 0 && privateSources.length === 0 && (
                <p style={{ textAlign: 'center' }}>No sources found.</p>
              )}
            </>
          )}
        </>
      )}
      {message && <p style={{ marginTop: '15px', textAlign: 'center', color: 'red' }}>{message}</p>}
    </div>
  );
}

export default ListSources;
