import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function Home() {
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [showSpinner, setShowSpinner] = useState(false);
  const conversationBoxRef = useRef(null);
  const { mysource } = useParams();

  useEffect(() => {
    // Scroll to bottom of conversation when messages change
    if (conversationBoxRef.current) {
      conversationBoxRef.current.scrollTop = conversationBoxRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    const message = e.target.message.value.trim();
    if (message === '') return;

    e.target.message.value = '';
    setIsLoading(true);
    setShowSpinner(true);

    const newMessage = { text: message, sender: 'user' };
    setMessages(prevMessages => [...prevMessages, newMessage]);

    try {
      console.log('Sending request to backend:', { question: message, source: mysource });
      const resp = await axios.post('/chatbot1/search/', {
        question: message,
        source: mysource
      });

      console.log('Full response from backend:', resp);

      if (resp.data && resp.data.response) {
        let response = resp.data.response;

        const botMessage = {
          text: response,
          sender: 'bot',
          copyButton: true,
          filenames: resp.data.references ? resp.data.references.map((ref) => ref.filename) : [],
        };

        setMessages(prevMessages => {
          console.log('Updating messages with bot response:', [...prevMessages, botMessage]);
          return [...prevMessages, botMessage];
        });
      } else {
        console.error('Unexpected response format:', resp.data);
        setMessages(prevMessages => [
          ...prevMessages,
          { text: 'Error: Unexpected response format', sender: 'bot' }
        ]);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      if (error.response) {
        console.error('Error response:', error.response.data);
        console.error('Error status:', error.response.status);
      } else if (error.request) {
        console.error('No response received:', error.request);
      } else {
        console.error('Error setting up request:', error.message);
      }
      setMessages(prevMessages => [
        ...prevMessages,
        { text: 'Error: Could not get response from server', sender: 'bot' }
      ]);
    } finally {
      setIsLoading(false);
      setShowSpinner(false);
    }
  };

  const handleCopyMessage = (text) => {
    navigator.clipboard.writeText(text).then(() => {
      alert('Message copied to clipboard!');
    }).catch(err => {
      console.error('Failed to copy message: ', err);
    });
  };

  return (
    <div className="chat-container">
      <h1>Chat Interface</h1>
      <div ref={conversationBoxRef} className="conversation-box">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.text}
            {msg.copyButton && (
              <button onClick={() => handleCopyMessage(msg.text)} className="copy-button">
                Copy
              </button>
            )}
            {msg.filenames && msg.filenames.length > 0 && (
              <div className="references">
                References: {msg.filenames.join(', ')}
              </div>
            )}
          </div>
        ))}
      </div>
      <form onSubmit={handleSendMessage}>
        <input type="text" name="message" placeholder="Type your message..." disabled={isLoading} />
        <button type="submit" disabled={isLoading}>Send</button>
      </form>
      {showSpinner && <div className="spinner">Loading...</div>}
    </div>
  );
}

export default Home;
