import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ChatWithSource = () => {
    const { sourceName } = useParams();
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const conversationBoxRef = useRef(null);

    useEffect(() => {
        if (conversationBoxRef.current) {
            conversationBoxRef.current.scrollTop = conversationBoxRef.current.scrollHeight;
        }
    }, [messages]);

    useEffect(() => {
        // Handle loading new data when sourceName changes if needed
        // You can add logic to fetch initial state related to the new sourceName
    }, [sourceName]);

    const sendMessage = async () => {
        if (!input) return;

        const newMessage = { text: input, user: 'Me' };
        setMessages((prevMessages) => [...prevMessages, newMessage]);
        setInput('');

        try {
            setIsLoading(true);
            const response = await axios.post(`/chat-with-source/${sourceName}/`, {
                question: input,
                filters: JSON.stringify({})
            });
            console.log('Received response from API:', response.data);
            const formattedContent = formatResponse(response.data.response.content);
            setMessages((prevMessages) => [...prevMessages, newMessage, { text: formattedContent, user: 'Bot' }]);
        } catch (error) {
            console.error('Error sending message:', error);
            setMessages((prevMessages) => [...prevMessages, newMessage, { text: 'Sorry, an error occurred while processing your request.', user: 'Bot' }]);
        } finally {
            setIsLoading(false);
        }
    };

    const formatResponse = (response) => {
        // Custom formatting logic
        response = response.replace(/\[(.*?)\]/g, '\n\n');
        response = response.replace(/(\w+)\.\n\n/g, '$1\n');
        response = response.replace(/\.\n\n/g, '.\n');
        response = response.replace(/^\s+|\s+$/g, '');
        response = response.replace(/^"|"$/g, '');
        response = response.replace(/\.\s+(\d+)\./g, '$1.');
        response = response.replace(/^\.\s*/gm, '');
        response = response.replace(/([^.\n])$/gm, '.');

        // Link and email formatting
        const linkRegex = /(https?:\/\/[^\s]+)/g;
        const urlRegex = /go\/([^\s]+)/g;
        const emailRegex = /([a-zA-Z0-9._-]+@[a-zA-Z0-9._-]+\.[a-zA-Z0-9_-]+)/g;

        response = response.replace(linkRegex, (match, url) => `<a href="${url}" target="_blank" rel="noopener noreferrer">${url}</a>`);
        response = response.replace(urlRegex, (match, sometext) => `<a href="http://go/${sometext}" target="_blank" rel="noopener noreferrer">go/${sometext}</a>`);
        response = response.replace(emailRegex, (match, email) => `<a href="mailto:${email}">${email}</a>`);

        return response;
    };

    return (
        <div>
            <h2>Chat with Source: {sourceName}</h2>
            <div ref={conversationBoxRef}>
                {messages.map((message, index) => (
                    <div key={index}>
                        <strong>{message.user}:</strong> <span dangerouslySetInnerHTML={{ __html: message.text }} />
                    </div>
                ))}
            </div>
            <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyPress={(e) => (e.key === 'Enter' ? sendMessage() : null)}
                disabled={isLoading}
            />
            <button onClick={sendMessage} disabled={isLoading}>Send</button>
        </div>
    );
};

export default ChatWithSource;
