import React, { useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Header from './Header';
import ConversationBox from './ConversationBox';
import InputBox from './InputBox';
import './App.css';

function Chatbot() {
  const { mysource } = useParams();
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [showSpinner, setShowSpinner] = useState(false);
  const [question, setQuestion] = useState('');
  const conversationBoxRef = useRef(null);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    const message = e.target.message.value.trim();
    if (message !== '') {
      e.target.message.value = '';
      setIsLoading(true);
      setShowSpinner(true);
      setMessages([...messages, { text: message, sender: 'user' }]);

      const updatedMessages = [...messages];
      updatedMessages.push({ text: message, sender: 'user' });

      try {
        const resp = await axios.post('/chatbot1/search/', { question: message, mysource });
        let response = resp.data.response;

        response = response.replace(/\[(.*?)\]/g, '\n\n')
          .replace(/(\w+)\.\n\n/g, '$1\n')
          .replace(/\.\n\n/g, '.\n')
          .replace(/^\s+|\s+$/g, '')
          .replace(/^"|"$/g, '')
          .replace(/\.\s+(\d+)\./g, '$1.')
          .replace(/^\.\s*/gm, '')
          .replace(/([^.\n])$/gm, '.');

        const linkRegex = /(https?:\/\/[^\s]+)/g;
        response = response.replace(linkRegex, (match, url) => {
          return `<a href="${url}" target="_blank" rel="noopener noreferrer">${url}</a>`;
        });

        const urlRegex = /go\/([^\s]+)/g;
        response = response.replace(urlRegex, (match, sometext) => {
          const href = `http://go/${sometext}`;
          return `<a href="${href}" target="_blank" rel="noopener noreferrer">go/${sometext}</a>`;
        });

        const emailRegex = /([a-zA-Z0-9._-]+@[a-zA-Z0-9._-]+\.[a-zA-Z0-9_-]+)/g;
        response = response.replace(emailRegex, (match, email) => {
          const href = `mailto:${email}`;
          return `<a href="${href}">${email}</a>`;
        });

        setMessages((prevMessages) => [
          ...prevMessages,
          {
            text: response,
            sender: 'bot',
            copyButton: true,
            filenames: resp.data.references ? resp.data.references.map((ref) => ref.filename) : [],
          },
        ]);

        setShowSpinner(false);

        if (conversationBoxRef.current) {
          conversationBoxRef.current.scrollTop = conversationBoxRef.current.scrollHeight;
        }
      } catch (error) {
        console.error(error);
      }

      setIsLoading(false);
      setShowSpinner(false);
    }
  };

  const chatbotClassName = mysource === 'public' ? 'Chatbot-public' : 'Chatbot-internal';
  const conversationBoxClassName = mysource === 'public' ? 'conversation-box-public' : 'conversation-box-internal';

  return (
    <div className={`Chatbot ${chatbotClassName}`}>
      <Header />
      <ConversationBox className={conversationBoxClassName} messages={messages} isLoading={isLoading} conversationBoxRef={conversationBoxRef} />
      <InputBox question={question} setQuestion={setQuestion} handleSendMessage={handleSendMessage} isLoading={isLoading} showSpinner={showSpinner} />
      {mysource === 'public' && <h1>Use the Illiad Public Source</h1>}
      {mysource === 'internal' && <h1>Use the Illiad Internal Source</h1>}
    </div>
  );
}

export default Chatbot;
